import React from "react";
import AddPuppy from "./AddPuppy";

const DisplayAllPuppies = ({
  players,
  getSinglePuppy,
  apiUrl,
  setPuppyPlayers,
}) => {
  //create a seperate file to run my remove function
  //filter through my id  and find the one that was click and remove it
  const removePuppy = async (puppyId) => {
    try {
      const response = await fetch(`${apiUrl}/${puppyId}`, {
        method: "DELETE",
      });
      const result = await response.json();
      console.log(result);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
      <div id="all-players-container">
        {players
          ? players.players.map((player) => {
              return (
                <div className="single-player-card" key={player.id}>
                  <div className="header-info">
                    <p className="pup-title">{player.name}</p>
                    <p className="pup-number">{player.id}</p>
                  </div>
                  <img src={player.imageUrl} alt="the puppy" />
                  <button
                    onClick={() => {
                      getSinglePuppy(player.id);
                    }}
                    className="detail-button"
                    data-id={player.id}
                  >
                    See details
                  </button>
                  <button
                    className="delete-button"
                    data-id={player.id}
                    onClick={() => {
                      removePuppy(player.id);
                    }}
                  >
                    Remove from roster
                  </button>
                </div>
              );
            })
          : "...Loading"}
      </div>
    </>
  );
};

export default DisplayAllPuppies;
