import React, { useState } from "react";

const AddPuppy = ({ apiUrl, players, setPuppyPlayers }) => {
  //Hold my variables in state
  const [puppyName, setPuppyName] = useState("");
  const [puppyBreed, setPuppyBreed] = useState("");
  console.log(players);

  //Craete another file that will run my async function

  //onSubmit add the player to our object
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(puppyName);
    console.log(puppyBreed);
    setPuppyPlayers([
      {
        id: players.length + 1,
        name: puppyName,
        imageUrl:
          "http://r.ddmcdn.com/w_1012/s_f/o_1/cx_0/cy_0/cw_1012/ch_1518/APL/uploads/2019/12/Crumpet-PBXVI.jpg",
      },
      ...players,
    ]);
  };
  const handleChange = (event) => {
    setPuppyName(event.target.value);
  };

  return (
    <div>
      {" "}
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={puppyName}
          onChange={handleChange}
        />
        <label>Breed:</label>
        <input
          type="text"
          name="breed"
          value={puppyBreed}
          onChange={(event) => {
            setPuppyBreed(event.target.value);
          }}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddPuppy;
