import React from "react";

const DisplayAllPuppies = ({ players, getSinglePuppy, apiUrl }) => {
  const handleSubmit = async (event) => {
    event.preventDefault();
    const addPlayer = {
      name: event.target.name.value, //How to access values from forms
      breed: event.target.breed.value, //
    };

    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: addPlayer.name,
          breed: addPlayer.breed,
        }),
      });
      const result = await response.json();
      console.log(result);
    } catch (err) {
      console.error(err);
    }
  };
  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" />
        <label>Breed:</label>
        <input type="text" name="breed" />
        <button type="submit">Submit</button>
      </form>
      <div id="all-players-container">
        {players
          ? players.players.map((player) => {
              return (
                <div className="single-player-card" key={player.id}>
                  <div className="header-info">
                    <p className="pup-title">{player.name}</p>
                    <p className="pup-number">{player.id}</p>
                  </div>
                  <img src={player.imageUrl} alt="the puppy" />
                  <button
                    onClick={() => {
                      getSinglePuppy(player.id);
                    }}
                    className="detail-button"
                    data-id={player.id}
                  >
                    See details
                  </button>
                  <button className="delete-button" data-id={player.id}>
                    Remove from roster
                  </button>
                </div>
              );
            })
          : "...Loading"}
      </div>
    </>
  );
};

export default DisplayAllPuppies;
