import React from "react";
import removePuppy from "./removePlayer";

const DeletePuppy = ({ player, setPuppyPlayers, players }) => {
  console.log(players);
  const removePuppyFromState = () => {
    setPuppyPlayers(players.filter((player) => player.id === !player.id));
  };
  removePuppyFromState();

  return (
    <div>
      {" "}
      <button
        className="delete-button"
        data-id={player.id}
        onClick={() => {
          removePuppy(player.id);
        }}
      >
        Remove from roster
      </button>
    </div>
  );
};

export default DeletePuppy;
