import React from "react";
import removePuppy from "./removePlayer";

const DeletePuppy = ({ player, setPuppyPlayers, players, apiUrl }) => {
  const removePuppy = async (puppyId) => {
    const cohort = "2303-ftb-et-web-ft-react"; //Cohort
    const apiUrl = `https://fsa-puppy-bowl.herokuapp.com/api/${cohort}/players`; //url
    try {
      const response = await fetch(`${apiUrl}/${puppyId}`, {
        method: "DELETE",
      });
      const result = await response.json();
      console.log(result);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {" "}
      <button
        className="delete-button"
        data-id={player.id}
        onClick={removePuppy(player.id)}
      >
        Remove from roster
      </button>
    </div>
  );
};

export default DeletePuppy;
