import React, { useEffect, useState } from "react";

const App = () => {
  const cohort = "2303=ftb-et-web-ft"; //Cohort
  const apiUrl = `https://fsa-puppy-bowl.herokuapp.com/api/${cohort}/players`; //url

  const [puppyPlayers, setPuppyPlayers] = useState([]);

  useEffect(() => {
    const getAllPuppies = async () => {
      try {
        const response = await fetch(apiUrl); //Fetch data from url
        const data = await response.json(); //parse into json format

        setPuppyPlayers(data); // set puppy data to state
      } catch (error) {
        console.error(error);
      }
    };
    getAllPuppies();
  }, []);
  console.log(puppyPlayers);
  return (
    <div>
      {puppyPlayers.data.players?.map((player) => {
        <div className="single-player-card">
          <div className="header-info">
            <p className="pup-title">{player.breed}</p>
            <p className="pup-number">{player.id}</p>
          </div>
          <img src={player.imageUrl} alt="the puppy" />
          <button className="detail-button" data-id={player.id}>
            See details
          </button>
          <button className="delete-button" data-id={player.id}>
            Remove from roster
          </button>
        </div>;
      })}
    </div>
  );
};

export default App;
