import React from "react";

const AddPuppy = ({ apiUrl, setPuppyPlayers }) => {
  //onSubmit add the player to our object
  const handleSubmit = async (event) => {
    event.preventDefault();
    const addPlayer = {
      name: event.target.name.value, //How to access values from forms
      breed: event.target.breed.value, //
    };
    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: addPlayer.name,
          breed: addPlayer.breed,
        }),
      });
      const result = await response.json();
      setPuppyPlayers(result);

      console.log(result);
    } catch (err) {
      console.error(err);
    }
    event.target.name.value = "";
    event.target.breed.value = "";
  };
  return (
    <div>
      {" "}
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" />
        <label>Breed:</label>
        <input type="text" name="breed" />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddPuppy;
