import React, { useEffect, useState } from "react";
import DisplayAllPuppies from "./Components/DisplayAllPuppies";

const App = () => {
  const cohort = "2303=ftb-et-web-ft"; //Cohort
  const apiUrl = `https://fsa-puppy-bowl.herokuapp.com/api/${cohort}/players`; //url

  const [puppyPlayers, setPuppyPlayers] = useState([]);
  const [singlePuppy, setSinglePuppy] = useState({});

  useEffect(() => {
    const getAllPuppies = async () => {
      try {
        const response = await fetch(apiUrl); //Fetch data from url
        const data = await response.json(); //parse into json format

        setPuppyPlayers(data); // set puppy data to state
      } catch (error) {
        console.error(error);
      }
    };
    getAllPuppies();
  }, []);

  const getSinglePuppy = async (puppyId) => {
    //when player clicks on an individual dog we will render the single player data
    const response = await fetch(`${apiUrl}/${puppyId}`);
    const data = await response.json();
    setSinglePuppy(data);
  };
  console.log(singlePuppy);

  const DisplaySinglePuppy = ({ singlePuppy }) => {
    console.log(singlePuppy);
    return (
      <div class="single-player-view">
        {singlePuppy ? (
          <div>
            {" "}
            <div class="header-info">
              <p class="pup-title">{singlePuppy.name}</p>
              <p class="pup-number">#{singlePuppy.id}</p>
            </div>
            <p>Team: {singlePuppy.team ? playerObj.team.name : "Unassigned"}</p>
            <p>Breed: {singlePuppy.breed}</p>
            <img src={singlePuppy.imagleUrl} alt="" />
            <button id="see-all">Back to all players</button>
          </div>
        ) : (
          "...Loading"
        )}
      </div>
    );
  };

  return (
    <div>
      <form>
        <label>Name:</label>
        <input type="text" name="name" />
        <label>Breed:</label>
        <input type="text" name="breed" />
        <button type="submit">Submit</button>
      </form>
      <DisplayAllPuppies
        players={puppyPlayers.data}
        getSinglePuppy={getSinglePuppy}
      />
      {/* <DisplaySinglePuppy singlePuppy={singlePuppy.data} /> */}
    </div>
  );
};

export default App;
